mapper.select:
select * from (select a.*, rowNum rn from (select * from member
${search} like '%' || #{keyword} || '%'
order by MB_num desc) a) where rn between #{startRow} and #{endRow}

mapper.update:
update member set MB_pw=#{MB_pw}, MB_name=#{MB_name},
	MB_nickName=#{MB_nickName}, MB_tel=#{MB_tel}
where MB_id=#{MB_id}
